mapper.select:
select svn_user_id
from svn_user
where svn_user_name = #{svnUserName}

    select
    a.svn_repo_id, a.svn_repo_name, a.svn_repo_path, a.svn_repo_url, a.svn_repo_des, 
    b.svn_user_id, b.svn_user_name, b.svn_password, b.user_status, 
    b.svn_user_create_date, b.svn_user_modify_date, 
    c.svn_user_authz_id, c.svn_user_authz,
    c.svn_path, c.authz_status, c.svn_user_authz_create_date, c.svn_user_authz_modify_date,
    f.name 
    from svn_repo a 
    join svn_user b on (a.svn_repo_id = b.svn_repo_id) 
    join svn_user_authz c on (b.svn_user_id = c.svn_user_id  and b.svn_repo_id = c.svn_repo_id) 
    join sys_user f on (b.svn_user_name = f.username)
    
	    
			and b.svn_user_name = #{svnUserName}
		
		
			and a.svn_repo_name = #{svnRepoName}
		
		
			and c.svn_path = #{svnPath}
		
		
			and c.svn_user_authz = #{svnUserAuthz}
		
	
	
		
			order by ${sort} ${order}
		
		
			order by a.svn_repo_id
		
	
    
		limit #{offset}, #{limit}

	select count(*) from svn_repo a join svn_user b on (a.svn_repo_id = b.svn_repo_id) 
    join svn_user_authz c on (b.svn_user_id = c.svn_user_id and b.svn_repo_id = c.svn_repo_id) 
    join sys_user f on (b.svn_user_name = f.username)
	
	    
			and b.svn_user_name = #{svnUserName}
		
		
			and a.svn_repo_name = #{svnRepoName}
		
		
			and c.svn_path = #{svnPath}
		
		
			and c.svn_user_authz = #{svnUserAuthz}
